AND create_time >= #{gmtCreateStart1}
AND create_time <= #{gmtCreateEnd1}
GROUP BY order_id ) g ON g.id = z.id left join loan_process process on process.order_id = z.order_id left join loan_order loan on loan.id = z.order_id left join loan_financial_plan financial on financial.id = loan.loan_financial_plan_id left join loan_base_info base on base.id = loan.loan_base_info_id left join employee e on e.id = base.salesman_id where process.apply_license_plate_deposit_info !=1 and case when #{maxGroupLevel} = 0 then false when #{maxGroupLevel} = 1 then
base.salesman_id in
