SELECT DISTINCT e.id, e.NAME, count(z.order_id) as overdueSum, sum(financial.loan_amount) as overdueAmountSum
FROM loan_process_log z INNER JOIN ( SELECT SUBSTRING_INDEX( GROUP_CONCAT( id ORDER BY create_time DESC SEPARATOR ',' ), ',', 1 ) AS id FROM loan_process_log WHERE task_definition_key = "usertask_remit_review" AND action = 1
AND create_time >= #{gmtCreateStart1}
AND create_time <= #{gmtCreateEnd1}
GROUP BY order_id ) g ON g.id = z.id LEFT JOIN loan_process process ON process.order_id = z.order_id LEFT JOIN loan_order loan ON loan.id = z.order_id LEFT JOIN loan_financial_plan financial ON financial.id = loan.loan_financial_plan_id LEFT JOIN loan_base_info base ON base.id = loan.loan_base_info_id LEFT JOIN employee e ON e.id = base.salesman_id LEFT JOIN material_audit audit ON audit.id = loan.material_audit_id WHERE 1 = ( CASE WHEN audit.complete_material_date IS NULL THEN IF ( datediff( DATE_FORMAT( now(), '%Y-%m-%d' ), DATE_FORMAT( z.create_time, '%Y-%m-%d' ) ) > 1, "1", "0" ) else IF ( datediff( DATE_FORMAT( audit.complete_material_date, '%Y-%m-%d' ), DATE_FORMAT( z.create_time, '%Y-%m-%d' ) )>1, "1", "0" ) end ) and case when #{maxGroupLevel} = 0 then false when #{maxGroupLevel} = 1 then
base.salesman_id in
#{item}
false
else true end
and base.partner_id in
#{item}
group by e.id

