AND z.create_time >= #{gmtCreateStart1}
AND z.create_time <= #{gmtCreateEnd1}
and case when #{maxGroupLevel} = 0 then false when #{maxGroupLevel} = 1 then
base.salesman_id in
#{item}
false
else true end
and base.partner_id in
#{item}
)tal order by tal.orderId
